import{r as a,o as l,a as c,b as e,d as o,w as s,F as h,e as t,c as r}from"./app.087f62ff.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const u={},p=e("h1",{id:"launching-a-3270-emulator-from-zowe-explorer",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#launching-a-3270-emulator-from-zowe-explorer","aria-hidden":"true"},"#"),t(" Launching a 3270 emulator from Zowe Explorer")],-1),m=t("Many enterprise application development teams engaged in long-term projects have numerous tools, utilities, jobs, processes, and best practices that rely on a traditional 3270 development environment. Remote Connection Emulator (RCE) integrates traditional 3270 tools and practices into Z Open Editor via Zowe Explorer. It provides access to the mainframe for viewing unique information and mainframe tools and processes that are not available in Zowe Explorer. By using Remote Connection Emulator, you can connect to the remote system and open a full-screen emulator. Z Open Editor's RCE client is based on the same technology as the RCE client built into the IBM Developer for z/OS Eclipse client. See its "),f={href:"https://www.ibm.com/docs/en/adffz/developer-for-zos/17.0?topic=zos-remote-connection-emulator",target:"_blank",rel:"noopener noreferrer"},g=t("documentation here"),_=t(" for more information on various features and settings."),w=r('<p>Some key features of RCE are</p><ul><li>Many personal configuration options such as color mappings, fonts, screen size, etc.</li><li>Screen size adapts to browser window size.</li><li>Screen keyboard and personalized custom key mappings.</li><li>Advanced selection and &quot;cross-hair support&quot; for copy, cutting, and pasting text.</li><li>BiDi support, e.g. for languages such as Hebrew.</li></ul><p>Z Open Editor&#39;s RCE client depends on a web server that runs on IBM Remote System Explorer (RSE) API server. Once provisioned and configured, it can be accessed utilizing the RSE API profile a Z Open Editor user has created for Zowe Explorer. The information provided by the profile will be used to authenticate and check entitlement on the RSE API server before opening the 3270 session in the user&#39;s default Web browser. This action is added by Z Open Editor as a new context menu command in Zowe Explorer&#39;s tree view allowing the user to launch the 3270 emulator for a specific Zowe Explorer profile of type RSE API.</p><h2 id="requirements" tabindex="-1"><a class="header-anchor" href="#requirements" aria-hidden="true">#</a> Requirements</h2><h3 id="z-os-components" tabindex="-1"><a class="header-anchor" href="#z-os-components" aria-hidden="true">#</a> z/OS components</h3>',5),E=e("li",null,"RSE API 1.2.1 or higher, which is part of the z/OS Explorer 3.4.1 product needs to be installed for the host defined in the RSE profile.",-1),b=t("Emulator configuration, the RSE API "),R={href:"https://www.ibm.com/docs/en/explorer-for-zos/3.4?topic=customization-rseapienv-rse-api-configuration-file",target:"_blank",rel:"noopener noreferrer"},v=t("documentation"),x=t(" specifies four emulator parameters that can be configured.(ie RCE_LUNAME, RCE_ENABLE, RCE_PORT, and RCE_SECURITY)."),y=e("h3",{id:"client-components",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#client-components","aria-hidden":"true"},"#"),t(" Client components")],-1),C=t("The IBM Z Open Editor advanced capabilities VS Code user setting must be selected. See "),S=t("Activating Advanced Capabilities"),Z=t(" for details."),I=t("A VU license needs to be installed for the host defined in the RSE profile. See "),k=t("Activating Advanced Capabilities"),z=t(" for details."),A=e("li",null,"Zowe Explorer VS Code extension 3.1.1 or higher.",-1),O=r('<h2 id="how-to-launch-the-emulator" tabindex="-1"><a class="header-anchor" href="#how-to-launch-the-emulator" aria-hidden="true">#</a> How to launch the emulator</h2><p>Right click an RSE profile in the Zowe Explorer tree view and select the option to launch the 3270 emulator in an external web browser. Selecting this will prompt user to open the external website, choose Open to launch the emulator. When the web page opens in the browser a host login screen will be shown.</p><p><strong>NOTE</strong> The RCE 3270 emulator does not support token authentication at this time, users with token authentication in place with their RSE profile will be prompted for their user and password for the launch request.</p><h2 id="troubleshooting" tabindex="-1"><a class="header-anchor" href="#troubleshooting" aria-hidden="true">#</a> Troubleshooting</h2><ul><li>If the launch 3270 action does not appear in the list of actions for an RSE profile it is likely that either the IBM Z Open Editor advanced capabilities check box is not selected or Zowe Explorer is at a version older than 3.1.1.</li><li>If you get the message <strong>CRRZG5370E Timed out launching the 3270 emulator</strong>, or the message <strong>CRRZG5367E The 3270 emulator was unable to contact the host</strong>, it indicates that a connection could not be made to the host. Check that you have internet access to this host.</li><li>If you get the message, <strong>CRRZG5371E The 3270 emulator was unable to connect to the host with the specified userid and password</strong>, it indicates that a connection was successfully made to the host, however, the userid and/or password specified were not valid. Verfiy that your userid and password are correct.</li><li>If you get the message, <strong>CRRZG5372E The 3270 emulator requires a VU license, but none was installed on the host</strong>, it indicates that the host you are connecting to does not have a VU license installed. Contact your host administrator to install the VU license.</li><li>If you get the message, <strong>CRRZG5373E The 3270 emulator has been disabled on the host</strong>, it indicates that your host administrator has disabled the remote emulator functionality on the host. Contact your host administrator to change this.</li></ul>',5);function T(V,B){const n=a("ExternalLinkIcon"),i=a("RouterLink");return l(),c(h,null,[p,e("p",null,[m,e("a",f,[g,o(n)]),_]),w,e("ul",null,[E,e("li",null,[b,e("a",R,[v,o(n)]),x])]),y,e("ul",null,[e("li",null,[C,o(i,{to:"/Docs/advanced_activation.html"},{default:s(()=>[S]),_:1}),Z]),e("li",null,[I,o(i,{to:"/Docs/advanced_activation.html"},{default:s(()=>[k]),_:1}),z]),A]),O],64)}var M=d(u,[["render",T]]);export{M as default};
