import{r as p,o as c,a as l,b as s,d as a,w as r,F as i,e as n,c as t}from"./app.9a9d1e31.js";import{_ as u}from"./plugin-vue_export-helper.21dcd24c.js";const d={},k=s("h1",{id:"managing-developer-workspaces",tabindex:"-1"},[s("a",{class:"header-anchor",href:"#managing-developer-workspaces","aria-hidden":"true"},"#"),n(" Managing Developer Workspaces")],-1),m=s("h2",{id:"starting-a-sample-workspace",tabindex:"-1"},[s("a",{class:"header-anchor",href:"#starting-a-sample-workspace","aria-hidden":"true"},"#"),n(" Starting a sample workspace")],-1),h=n("The "),b={href:"https://docs.redhat.com/en/documentation/red_hat_openshift_dev_spaces/3.17/html-single/user_guide/index#getting-started-with-devspaces",target:"_blank",rel:"noopener noreferrer"},g=n("Getting started with Dev Spaces"),y=n(" section of the Dev Spaces user guide gives a great overview to creating and running workspaces."),f=n("To start a workspace using Z Open Editor and Zowe Explorer we provided an example in our own "),w=n("Quickstart tutorial"),v=n('. Remember, that you can perform the steps of the "Create a workspace with the Z Open Editor sample repository" section with the Red Hat Developer Sandbox as well as any other Red Hat OpenShift Dev Spaces deployment that has access to the public internet for cloning our Z Open Editor sample repository.'),_=s("h2",{id:"start-a-custom-workspace",tabindex:"-1"},[s("a",{class:"header-anchor",href:"#start-a-custom-workspace","aria-hidden":"true"},"#"),n(" Start a custom workspace")],-1),z=s("p",null,"To create a custom workspace with your own configuration settings you need to add a devfile to the source code Git repository that you want to load. Devfiles are yaml text files used for development environment customization. Use them to configure a devfile to suit your specific needs and share the customized devfile across multiple workspaces to ensure identical user experience and build, run, and deploy behaviors across your team.",-1),S=n("The devfile schema is being developed as an open specification at "),x={href:"https://devfile.io",target:"_blank",rel:"noopener noreferrer"},D=n("https://devfile.io"),O=n(". The Red Hat documentation provides an "),E={href:"https://access.redhat.com/documentation/en-us/red_hat_openshift_dev_spaces/3.17/html/user_guide/devfile-introduction",target:"_blank",rel:"noopener noreferrer"},C=n("overview and resource list"),M=n(" for learning more about devfiles and their syntax."),Z=s("p",null,"In addition to the default devfile, Dev Spaces allows users to use a customized devfile to create a workspace. These are two methods using your own devfile:",-1),T={href:"https://access.redhat.com/documentation/en-us/red_hat_openshift_dev_spaces/3.17/html/user_guide/getting-started-with-devspaces#starting-a-workspace-from-a-git-repository-url",target:"_blank",rel:"noopener noreferrer"},I=n("Provide your own devfile in the Git repository"),j=n(" with your source code and start your workspace by just providing the https url to your Git repository."),R={href:"https://access.redhat.com/documentation/en-us/red_hat_openshift_dev_spaces/3.17/html/user_guide/getting-started-with-devspaces#starting-a-workspace-from-a-raw-devfile-url",target:"_blank",rel:"noopener noreferrer"},B=n("Create a workspace from a raw devfile URL"),N=n(". For example, your organization could manage a repository of standardized devfiles that your team can use for different types of development projects pulling together standard sets of images and tools."),L=t(`<h3 id="creating-custom-devfiles" tabindex="-1"><a class="header-anchor" href="#creating-custom-devfiles" aria-hidden="true">#</a> Creating custom devfiles</h3><p>When creating a devfile ensure that it uses the IBM Developer for z/OS on Dev Spaces container image (called &quot;ibm-wazi-for-devspaces-sidecar&quot;) that provides the capabilities needed for enterprise development projects. You can copy the default devfile shown below, paste it into your projects&#39; Git repository, and make modifications.</p><p>Simple devfile for enterprise development projects using the IBM Developer for z/OS on Dev Spaces image:</p><div class="language-yaml ext-yml line-numbers-mode"><pre class="language-yaml"><code><span class="token key atrule">schemaVersion</span><span class="token punctuation">:</span> 2.3.0
<span class="token key atrule">metadata</span><span class="token punctuation">:</span>
  <span class="token key atrule">name</span><span class="token punctuation">:</span> idzee<span class="token punctuation">-</span>devspaces

<span class="token key atrule">components</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zowe
    <span class="token key atrule">volume</span><span class="token punctuation">:</span>
      <span class="token key atrule">size</span><span class="token punctuation">:</span> 100Mi
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> idzee<span class="token punctuation">-</span>terminal
    <span class="token key atrule">container</span><span class="token punctuation">:</span>
      <span class="token key atrule">image</span><span class="token punctuation">:</span> icr.io/wazi<span class="token punctuation">-</span>code/ibm<span class="token punctuation">-</span>wazi<span class="token punctuation">-</span>for<span class="token punctuation">-</span>devspaces<span class="token punctuation">-</span>sidecar<span class="token punctuation">:</span>latest
      <span class="token key atrule">memoryLimit</span><span class="token punctuation">:</span> 3072Mi
      <span class="token key atrule">mountSources</span><span class="token punctuation">:</span> <span class="token boolean important">true</span>
      <span class="token key atrule">volumeMounts</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zowe
          <span class="token key atrule">path</span><span class="token punctuation">:</span> /home/user/.zowe
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br></div></div><h3 id="example-devfile-for-hybrid-application-development" tabindex="-1"><a class="header-anchor" href="#example-devfile-for-hybrid-application-development" aria-hidden="true">#</a> Example devfile for hybrid application development</h3><p>If you are working on a hybrid application and want to combine COBOL development with a stack for Nodejs and MongoDB, you can modify the first sample above in the following way to combine content from two of the Dev Spaces stacks. In this case, the usage of the IBM Developer for z/OS on Dev Spaces <code>ibm-wazi-for-devspaces-sidecar</code> container is extended to also provide environment variables and endpoints for running a Nodejs app, as our image also contains everything needed for Nodejs development. It then also added another image called <code>mongodb-36-rhel7</code> that provides a pre-configured instance of a MongoDB that can be used for development. Also, you find two more mounted storage volumes for npm and mongo in addition to the Zowe profile storage the IBM Developer for z/OS on Dev Spaces image is using.</p><div class="language-yaml ext-yml line-numbers-mode"><pre class="language-yaml"><code><span class="token key atrule">schemaVersion</span><span class="token punctuation">:</span> 2.2.0
<span class="token key atrule">metadata</span><span class="token punctuation">:</span>
  <span class="token key atrule">name</span><span class="token punctuation">:</span> wazi<span class="token punctuation">-</span>devspaces
<span class="token key atrule">components</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> wazi<span class="token punctuation">-</span>node
    <span class="token key atrule">container</span><span class="token punctuation">:</span>
      <span class="token key atrule">image</span><span class="token punctuation">:</span> icr.io/wazi<span class="token punctuation">-</span>code/ibm<span class="token punctuation">-</span>wazi<span class="token punctuation">-</span>for<span class="token punctuation">-</span>devspaces<span class="token punctuation">-</span>sidecar<span class="token punctuation">:</span>latest
      <span class="token key atrule">memoryLimit</span><span class="token punctuation">:</span> 3072Mi
      <span class="token key atrule">env</span><span class="token punctuation">:</span>
        <span class="token comment"># The values below are used to set up the environment for running the application</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> SECRET
          <span class="token key atrule">value</span><span class="token punctuation">:</span> 220fd770<span class="token punctuation">-</span>c028<span class="token punctuation">-</span>480d<span class="token punctuation">-</span>8f95<span class="token punctuation">-</span>f84353c7d55a
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> NODE_ENV
          <span class="token key atrule">value</span><span class="token punctuation">:</span> production
      <span class="token key atrule">endpoints</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">exposure</span><span class="token punctuation">:</span> public
          <span class="token key atrule">name</span><span class="token punctuation">:</span> nodejs
          <span class="token key atrule">targetPort</span><span class="token punctuation">:</span> <span class="token number">8080</span>
      <span class="token key atrule">mountSources</span><span class="token punctuation">:</span> <span class="token boolean important">true</span>
      <span class="token key atrule">sourceMapping</span><span class="token punctuation">:</span> /projects
      <span class="token key atrule">volumeMounts</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zowe
          <span class="token key atrule">path</span><span class="token punctuation">:</span> /home/user/.zowe
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> npm
          <span class="token key atrule">path</span><span class="token punctuation">:</span> /home/user/.npm
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> mongo
    <span class="token key atrule">container</span><span class="token punctuation">:</span>
      <span class="token key atrule">image</span><span class="token punctuation">:</span> registry.redhat.io/rhscl/mongodb<span class="token punctuation">-</span>36<span class="token punctuation">-</span>rhel7<span class="token punctuation">:</span>1<span class="token punctuation">-</span><span class="token number">50</span>
      <span class="token key atrule">env</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> MONGODB_USER
          <span class="token key atrule">value</span><span class="token punctuation">:</span> user
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> MONGODB_PASSWORD
          <span class="token key atrule">value</span><span class="token punctuation">:</span> password
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> MONGODB_DATABASE
          <span class="token key atrule">value</span><span class="token punctuation">:</span> guestbook
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> MONGODB_ADMIN_PASSWORD
          <span class="token key atrule">value</span><span class="token punctuation">:</span> password
      <span class="token key atrule">endpoints</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> mongodb
          <span class="token key atrule">exposure</span><span class="token punctuation">:</span> internal
          <span class="token key atrule">targetPort</span><span class="token punctuation">:</span> <span class="token number">27017</span>
      <span class="token key atrule">memoryLimit</span><span class="token punctuation">:</span> 512Mi
      <span class="token key atrule">mountSources</span><span class="token punctuation">:</span> <span class="token boolean important">false</span>
      <span class="token key atrule">volumeMounts</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> mongo<span class="token punctuation">-</span>storage
          <span class="token key atrule">path</span><span class="token punctuation">:</span> /var/lib/mongodb/data
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zowe
    <span class="token key atrule">volume</span><span class="token punctuation">:</span>
      <span class="token key atrule">size</span><span class="token punctuation">:</span> 100Mi
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> npm
    <span class="token key atrule">volume</span><span class="token punctuation">:</span>
      <span class="token key atrule">size</span><span class="token punctuation">:</span> 1G
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> mongo<span class="token punctuation">-</span>storage
    <span class="token key atrule">volume</span><span class="token punctuation">:</span>
      <span class="token key atrule">size</span><span class="token punctuation">:</span> 1G
<span class="token key atrule">commands</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> <span class="token key atrule">id</span><span class="token punctuation">:</span> 1<span class="token punctuation">-</span>run
    <span class="token key atrule">exec</span><span class="token punctuation">:</span>
      <span class="token key atrule">label</span><span class="token punctuation">:</span> <span class="token string">&quot;Run the application&quot;</span>
      <span class="token key atrule">component</span><span class="token punctuation">:</span> nodejs
      <span class="token key atrule">workingDir</span><span class="token punctuation">:</span> $<span class="token punctuation">{</span>PROJECTS_ROOT<span class="token punctuation">}</span>/nodejs<span class="token punctuation">-</span>mongodb<span class="token punctuation">-</span>sample
      <span class="token key atrule">commandLine</span><span class="token punctuation">:</span> <span class="token string">&quot;npm install &amp;&amp; node --inspect=9229 app.js&quot;</span>
      <span class="token key atrule">group</span><span class="token punctuation">:</span>
        <span class="token key atrule">kind</span><span class="token punctuation">:</span> run
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br><span class="line-number">33</span><br><span class="line-number">34</span><br><span class="line-number">35</span><br><span class="line-number">36</span><br><span class="line-number">37</span><br><span class="line-number">38</span><br><span class="line-number">39</span><br><span class="line-number">40</span><br><span class="line-number">41</span><br><span class="line-number">42</span><br><span class="line-number">43</span><br><span class="line-number">44</span><br><span class="line-number">45</span><br><span class="line-number">46</span><br><span class="line-number">47</span><br><span class="line-number">48</span><br><span class="line-number">49</span><br><span class="line-number">50</span><br><span class="line-number">51</span><br><span class="line-number">52</span><br><span class="line-number">53</span><br><span class="line-number">54</span><br><span class="line-number">55</span><br><span class="line-number">56</span><br><span class="line-number">57</span><br><span class="line-number">58</span><br><span class="line-number">59</span><br><span class="line-number">60</span><br><span class="line-number">61</span><br><span class="line-number">62</span><br><span class="line-number">63</span><br><span class="line-number">64</span><br></div></div><h3 id="example-devfile-for-integrating-z-os-connect-designer" tabindex="-1"><a class="header-anchor" href="#example-devfile-for-integrating-z-os-connect-designer" aria-hidden="true">#</a> Example devfile for integrating z/OS Connect Designer</h3>`,8),q=n("You can combine IBM z/OS Connect Designer with Wazi for Dev Spaces, providing fully integrated workspaces that allow you to graphically develop your APIs based on your COBOL source and edit and test generated code all in one place. The "),G={href:"https://www.ibm.com/docs/en/zos-connect/zos-connect/3.0?topic=pzcdw-create-edit-zos-connect-api-project-in-red-hat-openshift",target:"_blank",rel:"noopener noreferrer"},A=n("z/OS Connect Designer documentation"),P=n(" provides devfile examples for Eclipse Che that you can combine with our IBM Developer for z/OS on Dev Spaces devfile as follows:"),V=t(`<div class="language-yaml ext-yml line-numbers-mode"><pre class="language-yaml"><code><span class="token key atrule">schemaVersion</span><span class="token punctuation">:</span> 2.2.0
<span class="token key atrule">metadata</span><span class="token punctuation">:</span>
  <span class="token key atrule">name</span><span class="token punctuation">:</span> api<span class="token punctuation">-</span>project<span class="token punctuation">-</span>name
<span class="token key atrule">components</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> wazi
    <span class="token key atrule">container</span><span class="token punctuation">:</span>
      <span class="token key atrule">image</span><span class="token punctuation">:</span> icr.io/wazi<span class="token punctuation">-</span>code/ibm<span class="token punctuation">-</span>wazi<span class="token punctuation">-</span>for<span class="token punctuation">-</span>devspaces<span class="token punctuation">-</span>sidecar<span class="token punctuation">:</span>latest
      <span class="token key atrule">memoryLimit</span><span class="token punctuation">:</span> 3072Mi
      <span class="token key atrule">mountSources</span><span class="token punctuation">:</span> <span class="token boolean important">true</span>
      <span class="token key atrule">sourceMapping</span><span class="token punctuation">:</span> /projects
      <span class="token key atrule">volumeMounts</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zowe
          <span class="token key atrule">path</span><span class="token punctuation">:</span> /home/user/.zowe
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zowe
    <span class="token key atrule">volume</span><span class="token punctuation">:</span>
      <span class="token key atrule">size</span><span class="token punctuation">:</span> 100Mi
  <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> zosconnect
      <span class="token key atrule">container</span><span class="token punctuation">:</span>
        <span class="token key atrule">endpoints</span><span class="token punctuation">:</span>
          <span class="token punctuation">-</span> <span class="token key atrule">exposure</span><span class="token punctuation">:</span> public
            <span class="token key atrule">name</span><span class="token punctuation">:</span> zosconnect<span class="token punctuation">-</span>ui
            <span class="token key atrule">path</span><span class="token punctuation">:</span> /zosConnect/designer/
            <span class="token key atrule">protocol</span><span class="token punctuation">:</span> http
            <span class="token key atrule">targetPort</span><span class="token punctuation">:</span> <span class="token number">9080</span>
        <span class="token key atrule">env</span><span class="token punctuation">:</span>
          <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> ZCON_DESIGNER_PROJECT
            <span class="token key atrule">value</span><span class="token punctuation">:</span> /projects/api<span class="token punctuation">-</span>project<span class="token punctuation">-</span>name/apiProject
        <span class="token key atrule">image</span><span class="token punctuation">:</span> <span class="token string">&#39;icr.io/zosconnect/ibm-zcon-designer:3.0.78&#39;</span>
        <span class="token key atrule">memoryLimit</span><span class="token punctuation">:</span> 4927M
        <span class="token key atrule">mountSources</span><span class="token punctuation">:</span> <span class="token boolean important">true</span>
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br></div></div><p>The example above shows how the Wazi and z/OS Connect images get pulled into separate containers in your workspace: one running the z/OS Connect Designer web server and the other running development tools. From within your workspace you can access both containers via terminals. Both container specification mount the common sources folder so source code generated by the z/OS Connect Designer container can be accessed and edited by tools provided by the Wazi container.</p><h2 id="configuring-zowe-explorer" tabindex="-1"><a class="header-anchor" href="#configuring-zowe-explorer" aria-hidden="true">#</a> Configuring Zowe Explorer</h2><p>Next to the Z Open Editor VS Code extension, Zowe Explorer is key to the z/OS Dev Spaces user experience. Zowe Explorer works inside Dev Spaces almost in the same ways as for VS Code for Desktop with some minor exceptions as described in this section.</p><h3 id="secure-credential-management" tabindex="-1"><a class="header-anchor" href="#secure-credential-management" aria-hidden="true">#</a> Secure credential management</h3>`,5),W=n("On the desktop, Zowe CLI and Zowe Explorer use their "),F={href:"https://docs.zowe.org/stable/user-guide/cli-using-team-managing-credential-security",target:"_blank",rel:"noopener noreferrer"},U=n("secure credentials store"),H=n(", which stores credentials to z/OS systems in a local operation system specific way, such as the Keychain on MacOS. Dev Spaces runs on OpenShift and the same credentials management technology is not available. Therefore, Zowe CLI and Zowe Explorer need to be configured differently when running here."),J=t('<p>When you create a workspace in Dev Spaces that loads Zowe Explorer, the first time it starts up it will prompt you with a message that the default credentials manager could not be found and was disabled. The dialog then ask you to reload the current browser window and provide a button to do so. After it restarts Zowe Explorer will be ready to be used. Zowe Explorer disabled the credentials manager by changing a <strong>Remote</strong> VS Code setting that you can find following these steps:</p><ol><li>Go to the <strong>Remote</strong> settings tab of the Settings editor.</li><li>Search for the word &quot;Security&quot;.</li><li>Conform that the setting <strong>Zowe</strong> &gt; <strong>Security: Secure Credentials Enabled</strong> (<code>zowe.security.secureCredentialsEnabled</code>) is disabled.</li></ol><p>This has switched Zowe Explorer and Zowe CLI to not look for a secure credentials manager. Under the cover, it also modified the file <code>~/.zowe/settings/imperative.json</code> in the workspace user&#39;s home directory.</p><h3 id="team-configuration-file" tabindex="-1"><a class="header-anchor" href="#team-configuration-file" aria-hidden="true">#</a> Team configuration file</h3><p>Now, you can start creating a team configuration file as documented in <a href="./creating_team_profiles">Creating a configuration profile in Zowe Explorer v3.0.0</a>. However, in the json file, you need to remove all entries <code>&quot;secure&quot;: []</code> as there is no credentials manager configured that will use these entries.</p><p>The property <code>&quot;autoStore&quot;: false</code> will disable storing any credentials inside the team configuration file. Set it to <code>true</code> to enable it. If disabled, you will be prompted for username and password credentials every time your start using the Zowe Explorer views. The views remember these credentials in memory until you click the <strong>Refresh</strong> button in the Zowe Explorer views or reload or restart your workspace. If set enabled, you can work with credentials stored inside the team configuration file in two ways:</p><ul><li>When you request content for the first time, such as loading a list of data sets into the Data Sets view, you get prompted for username and password credentials. Once entered, those values will be saved and persisted in your team configuration file. This file is stored inside an encrypted volume in OpenShift that only you and administrator can access.</li><li>Instead of waiting for a user prompt, you can use token-based authentication that provides you with a time-limited access token. This token will also be stored in the team configuration file, but it will expire after a while (determined by your RAE API or z/OSMF administrator), so that OpenShift administrators will not be able to use it after its expiration. To request a token, simply right-click a profile in any Zowe Explorer view and select <strong>Login to Authentication Service</strong>. To learn more about this authentication, see the instructions that are detailed in the section <a href="./zowe_advanced_connection_scenarious">Single sign-on support for RSE profiles in Zowe Explorer</a>.</li></ul>',7),K=s("strong",null,"Note",-1),Y=n(": The Zowe project is working on a new capability called "),Q={href:"https://github.com/zowe/zowe-cli-secrets-for-kubernetes",target:"_blank",rel:"noopener noreferrer"},$=n("Secure Credential Store Plug-in for Zowe CLI"),X=n(", which will make configuring credentials consistent with managing secrets in Kubernetes and how secrets are managed in IBM Wazi for Dev Spaces as described on the page "),nn=n("Managing secrets"),sn=n(". Until this technology is fully tested and integrated into Wazi for Dev Spaces, you can try it out from the "),an={href:"https://github.com/zowe/zowe-cli-secrets-for-kubernetes",target:"_blank",rel:"noopener noreferrer"},en=n("open source Git repository"),tn=n(" following the instructions provided there.");function on(pn,rn){const e=p("ExternalLinkIcon"),o=p("RouterLink");return c(),l(i,null,[k,m,s("p",null,[h,s("a",b,[g,a(e)]),y]),s("p",null,[f,a(o,{to:"/Docs/cloud_developer_sandbox.html#create-a-workspace-with-the-z-open-editor-sample-repository"},{default:r(()=>[w]),_:1}),v]),_,z,s("p",null,[S,s("a",x,[D,a(e)]),O,s("a",E,[C,a(e)]),M]),Z,s("ul",null,[s("li",null,[s("a",T,[I,a(e)]),j]),s("li",null,[s("a",R,[B,a(e)]),N])]),L,s("p",null,[q,s("a",G,[A,a(e)]),P]),V,s("p",null,[W,s("a",F,[U,a(e)]),H]),J,s("p",null,[K,Y,s("a",Q,[$,a(e)]),X,a(o,{to:"/Docs/cloud_managing_secrets.html"},{default:r(()=>[nn]),_:1}),sn,s("a",an,[en,a(e)]),tn])],64)}var un=u(d,[["render",on]]);export{un as default};
