import{r as n,o as s,a as c,b as o,d as t,w as r,F as d,c as l,e}from"./app.00882f34.js";import{_ as u}from"./plugin-vue_export-helper.21dcd24c.js";const p={},f=l('<h1 id="connecting-to-z-os-using-zowe-cli-walk-through" tabindex="-1"><a class="header-anchor" href="#connecting-to-z-os-using-zowe-cli-walk-through" aria-hidden="true">#</a> Connecting to z/OS using Zowe CLI walk through</h1><h2 id="creating-a-zowe-team-configuration-file" tabindex="-1"><a class="header-anchor" href="#creating-a-zowe-team-configuration-file" aria-hidden="true">#</a> Creating a Zowe team configuration file</h2><ol><li><p>Issue the <code>zowe config init</code> command and you will be prompted for profile information. After responding to the prompts, the <code>zowe.config.json</code> file will be added to your <code>~/.zowe</code> directory.</p><ul><li>Run <code>zowe config init --global-config</code> to create a global profile.</li><li>Run <code>zowe config init --help</code> to learn all options for editing profiles by using a command line.<br></li></ul><p>Note: This tutorial walks through creating a global profile. User scoped configuration is very similar except that the file is created in your project&#39;s folder and is named <code>zowe.user.json</code>. Run <code>zowe config init --user-config</code> to create a profile scoped to your project folder.</p></li><li><p>If you responded to the prompts to enter a username and password for a mainframe service, your credentials are stored securely on your computer by default.</p></li><li><p>Open the <code>zowe.config.json</code> file in any text editor that supports JSON files, and then update the host and port values and other information needed in the profiles that are listed in the configuration file.</p></li><li><p>Fill out the <code>rse</code> or <code>zosmf</code> profile template inside <code>zowe.config.json</code> depending on which type of profile you are creating.</p></li><li><p>You can also use the command line to set team configuration properties. For example, run <code>zowe config set &quot;profiles.host1.profiles.service1.properties.setting&quot; &quot;value&quot; --global-config</code><code>zowe config set --secure ...</code> to set a secure property.</p></li><li><p>If username and password were entered in step 1, you can use your profiles to access data sets, z/OS UNIX System Services files and directories, and jobs. If not entered, you will be prompted the first time you run a CLI command. If \u2018autoStore:true\u2019 is set in the configuration file, the credentials will be stored securely on your computer.</p></li></ol>',3),h=e("For more information about team configuration profiles in Zowe, see "),m={href:"https://docs.zowe.org/stable/user-guide/cli-using-initializing-team-configuration",target:"_blank",rel:"noopener noreferrer"},_=e("Zowe CLI documentation"),g=e("."),w=e("For a Zowe team configuration file example, see "),z=e("here"),y=e("."),b=o("h2",{id:"supported-authentication-methods",tabindex:"-1"},[o("a",{class:"header-anchor",href:"#supported-authentication-methods","aria-hidden":"true"},"#"),e(" Supported authentication methods")],-1),x=e("Zowe supports many authentication mechanisms and you are not limited to username and password only. For more information, see "),k=e("Supported methods of authentication for z/OS using Zowe"),I=e(".");function S(v,C){const a=n("ExternalLinkIcon"),i=n("RouterLink");return s(),c(d,null,[f,o("p",null,[h,o("a",m,[_,t(a)]),g]),o("p",null,[w,t(i,{to:"/Docs/connect_to_zos_with_zowe_explorer_e2e.html#Zowe-Team-Configuration-file-example"},{default:r(()=>[z]),_:1}),y]),b,o("p",null,[x,t(i,{to:"/Docs/zowe_advanced_connection_scenarious.html"},{default:r(()=>[k]),_:1}),I])],64)}var F=u(p,[["render",S]]);export{F as default};
